config.script:
logger = system.util.getLogger('editEqLineConfig')
queryType = self.view.params.Type

try:
	params = self.view.custom.Params
	system.perspective.print(params)
	for key, value in params.items():
		if not value: raise ValueError('There was an error: The value for key {} is empty'.format(key))

	res = mes.config.sp.editLineConfig(params)
	if res is not True: raise ValueError(str(res))
	system.perspective.sendMessage('refreshTree')

except Exception as e:
	gpa.frontEnd.localUI.throwError(str(e))
else:
	gpa.frontEnd.localUI.throwSuccess('Updated')
