config.script:
import re
logger = system.util.getLogger('saveLineShiftConfig')

try:
    regex = '^([01]?\d|2[0-3]):([0-5]\d):([0-5]\d)$'
    params = self.view.custom.UpdateParams
    start_time = params.get('StartTime')

    if self.view.params.Type == 'add':
        params['LineID'] = self.view.params.LineID

    system.perspective.print(params)

    if start_time and re.match(regex, start_time):
        res = mes.config.sp.addEditLineShift(params)
        system.perspective.print(res)
        if res['status'] != 'success': 
            raise Exception(str(res['message']))
    else:
        raise Exception('Invalid Start Time recorded please use following format: "HH:mm:ss"')
except Exception as e:
    gpa.frontEnd.localUI.throwError(e)
else:
    gpa.frontEnd.localUI.throwSuccess('Shift configuration saved')
    system.perspective.sendMessage('refreshShiftsTable')
