config.script:
logger = system.util.getLogger('upsertScrapReasons')

try:
    params = self.view.custom.Params
    result = mes.config.sp.addEditScrapReason(params)

    if result['status'] != 'success': 
        raise ValueError('Sproc call failed. ' + result['message'])

    system.perspective.sendMessage('refreshScrapReason')
    system.perspective.closePopup(self.view.params.ModalName)
except Exception as e:
    errorParams = {
        "ModalName": "ErrorModal",
        "ErrorMessage": str(e),
    }

    system.perspective.openPopup("ErrorModal", "GlobalComponents/Modals/ErrorModal", errorParams)
    logger.warn('There was an error: ' + str(e))
