try:
    colName = 'ShiftPeriod'
    colData = []  # Variable initialized for storing new column data
    shiftNumberIndex = value.getColumnIndex('ShiftNumber')
    scheduleEndIndex = value.getColumnIndex('ScheduleEnd')  # Get index of ScheduleEnd column

    for i in range(value.getRowCount()):
        shiftNumber = value.getValueAt(i, shiftNumberIndex)
        scheduleEnd = value.getValueAt(i, scheduleEndIndex)

        # Check if ScheduleEnd is not empty and determine shift period
        if scheduleEnd is None or scheduleEnd == '':
            if shiftNumber == 1:
                period = 'day'
            elif shiftNumber == 2 or shiftNumber == 3 or shiftNumber == 4:
                period = 'night'
            else:
                raise ValueError('Invalid or no shift number provided')
        else:
            period = 'expired'  # Or any other appropriate value for expired shifts

        colData.append(period)  # Append to colData

    colCount = value.getColumnCount()
    newData = system.dataset.addColumn(value, colCount, colData, colName, str)
    return newData  # Return the new dataset

except Exception as e:
    system.perspective.print(str(e))

