transforms.code:
logger = system.util.getLogger('findingStateSubReasonCode')
subReason = self.getChild("root/SubReason/Dropdown").props.value

try:
	json = gpa.data.datasetToJson(self.params.statesDS)
	if subReason != -1:
		for j in json:
			if j.get('value') == value:
				return j.get('value')
	else: return -1

except Exception as e:
	logger.warn(str(e))

reason = value
try:
	json = gpa.data.datasetToJson(self.custom.SubReasons)

	for j in json:
		if j.get('value') == reason:
			return j.get('ID')
except Exception as e:
	return -1

logger = system.util.getLogger('filterStateDS')
try:
    json = gpa.data.datasetToJson(value)
    parentID = self.custom.Reason
    arr = []

    for j in json:
        if j.get('SubReasonOf') == parentID:
            arr.append(j)

    if len(arr) == 0 or arr == -1:
    	self.getChild("root/SubReason/Dropdown").props.value = -1
        return -1
    else:
        return arr

except Exception as e:
    logger.warn(str(e))

