queryPath = 'app/Equipment_Config/deleteState'
stateID = self.view.custom.SelectedRow[0].ID

params = { 'stateID': stateID }

try:
	system.db.runNamedQuery(queryPath, params)
	self.view.refreshBinding('custom.Data')
except Exception as e:
	errorParams = {
		"ModalName": "ErrorModal",
		"ErrorMessage": "The request could not be completed! " + str(e) 
	}

	system.perspective.openPopup("ErrorModal", "GlobalComponents/Modals/ErrorModal", errorParams)

logger = system.util.getLogger('attribute_export')

try:
	data = self.parent.parent.parent.getChild("Table").props.data
	csv = system.dataset.toCSV(dataset = data, showHeaders = True, forExport = True)
	fileName = "AttributeManager.csv"

	system.perspective.download(fileName, csv)

except Exception as e:
	errorParams = {
		"ModalName": "ErrorModal",
