try:
	selectedRowData = self.parent.parent.parent.getChild("Row2").getChild("Table").props.selection.data
	modalName = "editStates"
	modalPath = "App/EquipmentConfig/Embedded/Popups/AddEditStates"


	params = {
		"ModalName": modalName,
		"Data": selectedRowData,
		"Type": "edit"
	}

	if len(selectedRowData) > 0:
		system.perspective.openPopup(modalName, modalPath, params, title="Edit", modal=True, overlayDismiss=True)
	else: raise ValueError('A row was not selected')
except Exception as e:
	errorParams = {
		"ModalName": "ErrorModal",
		"ErrorMessage": 'There was an error: ' + str(e)
	}

	msg = 'There was an error: ' + str(e)
	system.perspective.openPopup("ErrorModal", "GlobalComponents/Modals/ErrorModal", errorParams)

logger = system.util.getLogger('attribute_import')

try:
	params = { 'Type': 'attributes' }
	system.perspective.openPopup("ImportFile", "GlobalComponents/Modals/ImportFile", params)

except Exception as e:
	errorParams = {
		"ModalName": "ErrorModal",
		"ErrorMessage": 'There was an error: ' + str(e)
	}

	msg = 'There was an error: ' + str(e)
	system.perspective.openPopup("ErrorModal", "GlobalComponents/Modals/ErrorModal", errorParams)

logger = system.util.getLogger('attribute_export')

try:
	data = self.parent.parent.parent.getChild("Table").props.data
	csv = system.dataset.toCSV(dataset = data, showHeaders = True, forExport = True)
	fileName = "AttributeManager.csv"

	system.perspective.download(fileName, csv)

except Exception as e:
	errorParams = {
		"ModalName": "ErrorModal",
		"ErrorMessage": 'There was an error: ' + str(e)
	}

	msg = 'There was an error: ' + str(e)
	system.perspective.openPopup("ErrorModal", "GlobalComponents/Modals/ErrorModal", errorParams)

queryPath = 'app/Equipment_Config/deleteState'
