config.script:
selectedRowData = self.parent.parent.parent.getChild("Row2/Segments").props.selection.data
modalName = "editProductCodeSegment"
modalPath = "App/Schedule/ProductCodes/Embedded/Popups/Edit/EditPCSegment"

params = {
	"ModalName": modalName,
	"RowData": selectedRowData
}

errorParams = {
	"ModalName": "ErrorModal",
	"ErrorMessage": "Please select a row first!"
}

if len(selectedRowData) > 0:
	system.perspective.openPopup(modalName, modalPath, params, title="Edit Segments", modal=True)
else:
	system.perspective.openPopup("ErrorModal", "GlobalComponents/Modals/ErrorModal", errorParams, modal=True)

queryPath = 'app/Schedule/deletePCSegment'
pcSegmentID = self.view.custom.RowData[0].ID

params = { 'id': pcSegmentID }

try:
	system.db.runNamedQuery(queryPath, params)
	self.view.refreshBinding('custom.Data')
except Exception as e:
	errorParams = {
		"ModalName": "ErrorModal",
		"ErrorMessage": "The request could not be completed! " + str(e) 
	}

	system.perspective.openPopup("ErrorModal", "GlobalComponents/Modals/ErrorModal", errorParams)
