transforms.code:
output_json = []
Approved = self.view.custom.TestStatusColors.Approved
AwaitingReview = self.view.custom.TestStatusColors.AwaitingReview
Unapproved = self.view.custom.TestStatusColors.Unapproved
Incomplete = self.view.custom.TestStatusColors.Incomplete
OnHold = self.view.custom.TestStatusColors.OnHold

for row in range(value.getRowCount()):
    row_object = {}

    row_value = {}
    row_style = {}
    for col in range(value.getColumnCount()):    
        row_value[value.getColumnName(col)] = value.getValueAt(row, col)
        row_object['value'] = row_value

        if value.getColumnName(col) == 'TestStatus':
            if value.getValueAt(row, col) == 'Incomplete':
                row_style = Incomplete
            elif value.getValueAt(row, col) == 'Approved':
                row_style = Approved
            elif value.getValueAt(row, col) == 'Awaiting Review':
                row_style = AwaitingReview
            elif value.getValueAt(row, col) == 'Unapproved':
                row_style = Unapproved
            elif value.getValueAt(row, col) == 'On Hold':
                row_style = OnHold

        row_object['style'] = row_style

    output_json.append(row_object)

return output_json

