onChange.script:
if currentValue.value is not None and currentValue.value != '':

	def look(dict, UUID, count):

		dict['expanded'] = True
		for index, item in enumerate(dict['items']):


			if item['data']['eqStateUUID'] == str(UUID):
				item['expanded'] = True
				self.getChild("root").getChild("content").getChild("Tree").props.selection = []
				self.getChild("root").getChild("content").getChild("Tree").props.selection.append(count + '/' + str(index))
			if item['items'] is not None:
				look(item, UUID, str(count)+'/'+str(index))

	UUID = currentValue.value
	items = self.getChild("root").getChild("content").getChild("Tree").props.items
	for item in items:
		look(item, UUID, "0")

else:
	def look(dict):
		dict['expanded'] = False
		for index, item in enumerate(dict['items']):
			item['expanded'] = False
			if item['items'] is not None:
				look(item)
	items = self.getChild("root").getChild("content").getChild("Tree").props.items			
	for item in items:
		look(item)			
	self.getChild("root").getChild("content").getChild("Tree").props.selection = []

