onChange.script:
	if currentValue.value is not None and currentValue.value != '':
		relativeUUID = currentValue.value
		obj = system.mes.loadMESObject(relativeUUID)
		type = obj.getMESObjectTypeName()


		def look(obj):
			list = obj.getParentCollection().getList()
			for item in list:

#				parentName = item.getName()
				return item

		l = []
		def findAllParents(obj, l = l):
			list = obj.getParentCollection().getList()
			for item in list:

				parentName = item.getName()
				if parentName != 'Equipment States':
					l.append(item.getMESObject())
					findAllParents(item.getMESObject())
		#	return parentName

		if type == 'EquipmentState':
			item = look(obj)
			parentUUID = item.getMESObjectUUID()
			parentName = item.getName()
		else:
			parentName = obj.getName()
			parentUUID = relativeUUID

#		parentName = look(obj)

		self.custom.parent = parentName
		self.custom.parentUUID = parentUUID
		findAllParents(obj)
		codes = []
		def findCodes(parent, codes = codes):

			parentObject = parent

			childList = parentObject.getChildCollection().getList()

			for child in childList:
				if child.getMESObjectTypeName() == 'EquipmentState':
					codes.append(child.getMESObject().getStateCode())
				else:
					findCodes(child.getMESObject(), codes)

		parents = l
		for parent in parents:
			findCodes(parent)
		if len(codes) > 0:
			code = max(codes) + 1
		else:
			code = 1

		self.getChild("data").getChild("assignedCodesDisplay").props.text = ', '.join(map(str, set(codes)))
		self.getChild("data").getChild("codeEntry").props.value = code
